const { findPacketStartPosition, findMessageStartPosition } = require('./day-6');

describe('day 6', () => {
  describe('part 1', () => {
    describe('findPacketStartPosition', () => {
      test('with a expects -1', () => {
        const data = 'a';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(-1);
      });
      test('with ababaab expects -1', () => {
        const data = 'ababaab';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(-1);
      });
      test('with abcd expects 4', () => {
        const data = 'abcd';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(4);
      });
      test('with abcde expects 4', () => {
        const data = 'abcde';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(4);
      });
      test('with aabcde expects 5', () => {
        const data = 'aabcde';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(5);
      });
      test('with example 1 expects 7', () => {
        const data = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(7);
      });
      test('with example 2 expects 5', () => {
        const data = 'bvwbjplbgvbhsrlpgdmjqwftvncz';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(5);
      });
      test('with example 3 expects 6', () => {
        const data = 'nppdvjthqldpwncqszvftbrmjlhg';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(6);
      });
      test('with example 4 expects 10', () => {
        const data = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(10);
      });
      test('with example 5 expects 11', () => {
        const data = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw';

        const startPosition = findPacketStartPosition(data);

        expect(startPosition).toBe(11);
      });
      test('with data', () => {
        const data = '';
        const startPosition = findPacketStartPosition(data);
        console.log(`Packet start position: ${startPosition}`);
      });
    });
  });
  describe('part 2', () => {
    describe('findMessageStartPosition', () => {
      test('with mjqjpqmgbljsphdztnvjfqwrcgsmlb expects 19', () => {
        const data = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb';

        const startPosition = findMessageStartPosition(data);

        expect(startPosition).toBe(19);
      });
      test('with bvwbjplbgvbhsrlpgdmjqwftvncz expects 23', () => {
        const data = 'bvwbjplbgvbhsrlpgdmjqwftvncz';

        const startPosition = findMessageStartPosition(data);

        expect(startPosition).toBe(23);
      });
      test('with nppdvjthqldpwncqszvftbrmjlhg expects 23', () => {
        const data = 'nppdvjthqldpwncqszvftbrmjlhg';

        const startPosition = findMessageStartPosition(data);

        expect(startPosition).toBe(23);
      });
      test('with nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg expects 29', () => {
        const data = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg';

        const startPosition = findMessageStartPosition(data);

        expect(startPosition).toBe(29);
      });
      test('with zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw expects 26', () => {
        const data = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw';

        const startPosition = findMessageStartPosition(data);

        expect(startPosition).toBe(26);
      });
      test('with data', () => {
        const data = '';
        const startPosition = findMessageStartPosition(data);
        console.log(`Message start position: ${startPosition}`);
      });
    });
  });
});
